import React, {useState} from "react";
import { ReactComponent as DocumentLogo } from "../assets/icons/article-24px.svg";
import axios from "axios";

import { BASE_URL } from "../utils/api";

function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleEmail = ({ target: { value } }) => {
    setEmail(value);
  };
  const handlePassword = ({ target: { value } }) => {
    setPassword(value);
  };

  const register = () =>{
      let body = {
          email : email,
          password : password
      }
      axios.post(`${BASE_URL}/register`, body)
      .then((response)=>{
          console.log(response.data.message);
      })
      .catch((error)=>{
          console.log(error);
      })
  }
  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col login-col shadow-lg">
          <DocumentLogo fill="white" height={60} width={60} />
          <h2>Register</h2>
          <input
            className="login-input-fields"
            type="text"
            placeholder="email"
            onChange={handleEmail}
            value={email}
          />
          <br />
          <input
            className="login-input-fields"
            type="password"
            placeholder="password"
            onChange={handlePassword}
            value={password}
          />
          <br />
          <button onClick={register} className="login-button">
            GO
          </button>
        </div>
      </div>
    </div>
  );
}

export default Register;
