import React, { useState, useEffect } from "react";
import axios from "axios";
import { BASE_URL } from "../utils/api";
import Document from "../components/document";

function Home() {
  const [documentArray, setDocuments] = useState([]);

  useEffect(() => {
    getDocuments();
  });
  const getDocuments = () => {
    var docArray = [];
    axios
      .get(`${BASE_URL}/document/`)
      .then((response) => {
        //console.log(response);
        var documents = response.data;
        documents.forEach((document) => {
          docArray = docArray.concat(document);
        });
        setDocuments(docArray);
        console.log(docArray);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const renderDocuments = () =>{
      console.log(documentArray);
   documentArray.map(({filename, author})=>{return <Document name={filename} author={author}/>});
  }
  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-4 home-col shadow-lg">
          <h2>Welcome Advyth</h2>
          <div className="image-bg shadow-lg">
            <img
              src="https://retro-avatar.s3.amazonaws.com/avatar_5c0533500f0dc351421d2c6c50717252.png"
              height={50}
              width={50}
            />
          </div>
          <div className="col-md-11 home-file-col">
            {renderDocuments()}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
