import React, { useState } from "react";
import { ReactComponent as DocumentLogo } from "../assets/icons/article-24px.svg";
import axios from "axios";

import {BASE_URL} from "../utils/api";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleEmail = ({target : {value}}) =>{
      setEmail(value);
  }
  const handlePassword = ({target : {value}}) =>{
      setPassword(value);
  }

  const login = () =>{
      let body = {
          email : email,
          password : password
      }
      axios.post(`${BASE_URL}/login`, body)
      .then((response)=>{
          alert(response);
      })
      .catch((error)=>{
          alert(error);
      })
  }

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col login-col shadow-lg">
          <DocumentLogo fill="white" height={60} width={60} />
          <h2>Login</h2>
          <input
            className="login-input-fields"
            type="text"
            placeholder="email"
            onChange={handleEmail}
          />
          <br />
          <input
            className="login-input-fields"
            type="password"
            placeholder="password"
            onChange={handlePassword}
          />
          <br />
          <button onClick={login} class="login-button">GO</button>
        </div>
      </div>
    </div>
  );
}

export default Login;
