import React, { useState } from "react";
import { ReactComponent as DocumentLogo } from "../assets/icons/article-24px.svg";
import axios from "axios";

import { BASE_URL } from "../utils/api";
import { _DEV_ } from "../utils/env_vars";

function Login() {
  const [email, setEmail] = useState(_DEV_ ? "advythashok@gmail.com" : "");
  const [password, setPassword] = useState(_DEV_ ? "password" : "");
  const [loading, setLoading] = useState(false);

  const handleEmail = ({ target: { value } }) => {
    setEmail(value);
  };
  const handlePassword = ({ target: { value } }) => {
    setPassword(value);
  };

  const login = () => {
    setLoading(true);
    let body = {
      email: email,
      password: password,
    };
    axios
      .post(`${BASE_URL}/login`, body)
      .then((response) => {
          setLoading(false);
        console.log(response.data.message);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col login-col shadow-lg">
          <DocumentLogo fill="white" height={60} width={60} />
          <h2>Login</h2>
          <input
            className="login-input-fields"
            type="text"
            placeholder="email"
            onChange={handleEmail}
            value={email}
          />
          <br />
          <input
            className="login-input-fields"
            type="password"
            placeholder="password"
            onChange={handlePassword}
            value={password}
          />
          <br />
          <button onClick={login} className="login-button">
          {loading ? (
              <span
                className="spinner-border spinner-border-sm"
                role="status"
                aria-hidden="true"
              ></span>
            ) : (
              "Login"
            )}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
